var signalhub = require('signalhub')
var swarm = require('webrtc-swarm')

var started = Date.now()

function test () {
  var hub = signalhub('benchmarking', ['http://localhost:10000'])
  var sw = swarm(hub, {
    wrtc: require('electron-webrtc')()
  })

  sw.on('peer', function () {
    console.log('got peer in', Date.now() - started, 'ms')
  })
}

test()
test()

